select r.rating, m.uuid as movie_uuid from ratings r 
	inner join _movie_uuid m on r.movie_id = m.movie_id 
    where r.user_id = ${record:value('/id')}

select t.tag, m.uuid as movie_uuid from tags t 
	inner join _movie_uuid m on t.movie_id = m.movie_id 
    where t.user_id = ${record:value('/id')}
